/*CSS for ConceptJS Video Gallery*/

body {
    background: #e0e7eb;
    color: #454545;
    font-family: 'Open Sans', sans-serif;
}

.title{
    border-bottom: 1px solid rgba(0,0,0,.1);
}

.col-centered{
    display: block;
    float: none !important;
    margin-left:auto;
    margin-right:auto;
}

.video-gallery{
    padding-top: 20px;
}

.video-player{
    height: 426px;
    width: 856px;
    margin-left: 10px;
    border-style: solid;
    border-color: gray;
    border-radius: 5px;
    border-width: 1px;
    padding-left: 0px;
}

.video-list{
    height: 426px;
    border-style: solid;
    border-color: gray;
    border-radius: 5px;
    border-width: 1px;
    overflow-y: scroll;
    overflow-x: hidden;
    max-width: 100%;
    margin-left: 15px;
    padding-left: 0px;
}

.search{
    margin-left: 0px; 
    padding-right: 16px;
    padding-bottom: 10px;
}

.right-inner-addon {
    position: relative;
}

.right-inner-addon input {
    padding-right: 30px;    
}

.right-inner-addon i {
    position: absolute;
    right: 0px;
    padding: 10px 12px;
    pointer-events: none;
}

.video-list-wrapper .row {
    margin-left: 0px;
}

.video-list-wrapper .overlay{
    transition: top .2s,opacity .2s;
    -webkit-transition: top .2s,opacity .2s;
    -moz-transition: top .2s,opacity .2s;
    -o-transition: top .2s,opacity .2s;
    -ms-transition: top .2s,opacity .2s;
    padding: 10px;
    height: 106px;
    z-index: 50;
    cursor: pointer;
    overflow: hidden;
    border-bottom: 1px solid rgba(0,0,0,.1);
}

.video-list-wrapper .overlay:hover,
.video-list-wrapper .overlay.active:hover{
    background: rgba(0,0,0,.75) !important;
    color: #FFFFFF !important;
    overflow: visible;
}

.video-list-wrapper .overlay.active{
    background: rgba(0,0,0,.25) !important;
    color: #FFFFFF !important;
}

.video-list-wrapper .overlay .video-title{
    font-size: 14px;
    font-weight: bold;
}

.video-list-wrapper .overlay .video-desc{
    font-size: 13px;
    padding-top: 10px;
    height: 75px;
}

#categoryList ul{
    margin-top: 11px;
    margin-left: -30px;
}

#categoryList li{
    display: inline;
    list-style-type: none;
    padding-right: 20px;
}

.sociallinks {
    padding-top: 65px;
    padding-left: 50px;
}

.sociallinks li{
    display: inline;
    list-style-type: none;
    padding-right: 2px;
}

.cjs-twitter-button {
    position: absolute !important;
}
