/* User Public Profile View CSS */
                body {
                    background-position: left 40px;
                    background-attachment: fixed;
                    background-repeat: repeat;
                    background-repeat: no-repeat;
                    background-color: #e9eaed;
                    font-family:"Helvetica Neue",Helvetica,Arial,sans-serif !important;
                }

                .ellipsis {
                    white-space: nowrap;
                    overflow: hidden;
                    text-overflow: ellipsis;
                    -o-text-overflow: ellipsis;
                    -ms-text-overflow: ellipsis;
                    -webkit-text-overflow: ellipsis;
                    -moz-text-overflow: ellipsis;
                }

                .topbar { 
                    background-color: #b2dba1; 
                    opacity: 0.7;
                    border-bottom: solid 2px #EEE; 
                    padding: 3px 0; 
                    text-align: center; 
                    color: #11100B;
                }

                .user-profile-text-hover-hand:hover{
                    cursor: hand;
                }

                .user-profile h1 {
                    display: inline-block;
                    font-size: 30px;
                    margin: 0 0px;
                    padding: 0px 0 0 0;
                    color: white;
                }

                .user-profile h2 {
                    display: inline-block;
                    font-size: 20px;
                    margin: 0 0px;
                    padding: 0px 0 0 0;
                    color: black;
                }

                .user-profile h3 {
                    display: inline-block;
                    font-size: 15px;
                    margin: 5px;
                    padding: 20px;
                    color: black;
                }

                .user-profile a {
                    color: #66757f;
                }

                .user-profile-divider{
                    height: 1px;
                    margin-right: 10px;
                    overflow: hidden;
                    background-color: #e5e5e5;
                }

                .user-profile-header{
                    background-color: #2C51A2;
                    background-size: 100%;
                    border-bottom: 1px solid #e1e8ed;
                    height: 250px;
                    border-radius: 5px 5px 0 0;
                    background-position: 0 50%;
                    width: 100%;
                    padding: 0 1px;
                    margin-top: 10px;
                } 

                .user-profile-info{
                    color: white;
                    margin-top: -155px;
                    margin-left: 260px;
                }

                .user-profile-info-follow{
                    margin-top: 15px;
                }

                .user-profile-info-email{
                    margin-top: 5px;
                }

                .user-profile-info-follow .col-md-2{
                    padding-left: 0px;
                }

                .user-profile-info-follower{
                    margin-left: -10px;
                }

                .user-profile-info a{
                    color: white;
                }

                .user-profile-info a:hover{
                    color: #32BB7B;
                }

                .effects {
                    padding-left: 15px;
                }

                .effects .img {
                    position: relative;
                    float: left;
                    margin-bottom: 5px;
                    margin-top: -180px;
                    margin-left: 30px;
                }

                .overlay {
                    display: block;
                    position: absolute;
                    z-index: 20;
                    background: rgba(0, 0, 0, 0.8);
                    overflow: hidden;
                    transition: all 0.5s;
                }

                .overlay .user-info{
                    padding: 20px;
                    color: white;
                    margin-left: 10px;
                }

                .overlay .user-info .row{
                    margin-top: 10px;
                }

                .overlay .user-info .bio{
                    line-height: 1.5;
                    font-size: 12px;
                    font-weight: 400;
                    height: 80px;
                }

                .overlay .user-info .website{
                    margin-top: 0px;
                }

                .overlay .user-info .website a{
                    color: white;
                }

                #slideUpInfo .overlay {
                    bottom: 0;
                    left: 0;
                    right: 0;
                    width: 100%;
                    height: 0;
                }

                #slideUpInfo .overlay a.expand {
                    left: 0;
                    right: 0;
                    bottom: 50%;
                    margin: 0 auto -30px auto;
                }

                #slideUpInfo .img.hover .overlay {
                    height: 100%;
                }

                .user-avatar-image{
                    border-radius: 5px;
                    height: 200px;
                    width: 200px;
                    display: inline-block!important;
                    border-style: solid;
                    border-width: 3px;
                    border-color: #f2f2f2;
                    display: block;
                    padding: 0;
                    max-width: 100%;
                }

                .user-avatar-image-item{
                    border-radius: 2px;
                    height: 50px;
                    width: 50px;
                    display: inline-block!important;
                    margin-left: 12px;
                    border-style: solid;
                    border-width: 3px;
                    border-color: #f2f2f2;
                }

                .user-profile-menu{
                    background: #f2f2f2;
                    border-radius: 0px 0px 5px 5px;
                    height: 46px;
                }

                .user-profile-menu ul{
                    padding-top: 0px;
                    padding-bottom: 0px;
                    margin-left: 280px;
                    margin-right: 10px;
                }

                .user-profile-menu ul li{
                    width: 130px;
                }

                .user-profile-menu ul li a{
                    border-radius: 0px;
                    font-size: 16px;
                }

                .user-profile-menu-header{
                    display: block;
                    padding: 3px 20px;
                    font-size: 15px;
                    line-height: 1.5;
                    color: gray;
                    white-space: nowrap;
                }

                .user-profile-menu-active a{
                    border-bottom-style: solid;
                    border-bottom-color: #32BB7B;
                    border-bottom-width: 4px;
                    color: black;
                }

                .nav li a:hover {
                    background-color: transparent;
                    border-bottom-style: solid;
                    border-bottom-color: #32BB7B;
                    border-bottom-width: 4px;
                    color: #32BB7B;
                    -webkit-transition: all 500ms;
                    -moz-transition: all 500ms;
                    -o-transition: all 500ms;
                    -ms-transition: all 500ms;
                    transition: all 500ms;
                }

                .user-profile-menu-icon{
                    padding-right: 5px;
                    cursor: pointer;
                }

                .user-profile-content{
                    padding-left: 10px;
                    background: #f2f2f2;
                    margin-top: 5px;
                    border-radius: 5px 5px 5px 5px;
                }

                .user-profile-bio{
                    padding-left: 10px;
                    background: #f2f2f2;
                    margin-top: 5px;
                    border-radius: 5px 5px 5px 5px;
                }

                .user-profile-content-header{
                    padding-left: 10px;
                }

                .user-profile-content-header .header{
                    padding-top: 8px;
                }

                .user-profile-content-header ul li a{
                    border-radius: 0px;
                    font-size: 14px;
                    height: 40px;
                }

                .user-profile-content-header .search{
                    padding-top: 4px;
                    padding-right: 35px;
                }

                .user-profile-content-header .search-input{
                    margin-top: 5px;
                    padding-right: 10px;
                    height: 30px;
                    width: 400px;
                    display: inline;
                }

                .right-inner-addon {
                    position: relative;
                }

                .right-inner-addon input {
                    padding-right: 30px;    
                }

                .right-inner-addon i {
                    position: absolute;
                    right: 0px;
                    padding: 10px 12px;
                    pointer-events: none;
                }

                .user-profile-content-header i{
                    padding-left: 5px;
                }

                .user-profile-content .frame{
                    margin-right: 10px;
                    padding-bottom: 20px;
                }

                .user-profile-content .exhibit-item{
                    height: 257px;
                    width: 350px;
                    padding: 2px;
                    background-color: rgba(128, 128, 128, 0.36);
                }

                .user-profile-content .item-footer{
                    background-color: rgba(128, 128, 128, 0.36);
                    width: 350;
                    margin-left: 10px;
                }

                .user-profile-content .item-footer .name{
                    width: 130px;
                    display:inline-block;
                    font-size: 14px;
                }

                .user-profile-content .item-footer .user{
                    width: 100px;
                    margin-top: 2px;
                    font-size: 12px;
                    position: absolute;
                    display:inline-block;
                }

                .fa-bars {
                    border: 1px solid transparent;
                    display: inline-block !important;
                    border-radius: 2px;
                    margin: 2px 5px 2px 5px;
                    min-width: 24px !important;
                    width: 24px !important;
                    z-index: 10;
                    padding: 2px 1px 3px 3px !important;
                    cursor: pointer;
                    margin-left: -10px
                }

                .fa-bars:hover{
                    background-color: white;
                    border: 1px solid;
                    display: inline-block !important;
                    border-radius: 2px;
                    margin: 2px 5px 2px 5px;
                    min-width: 24px !important;
                    width: 24px !important;
                    z-index: 10;
                    padding: 2px 1px 3px 3px !important;
                    cursor: pointer;
                    margin-left: -10px
                }

                .user-profile-content .item-footer-icon{
                    position: absolute;
                    right: 0px;
                    width: 120px;
                    padding-top: 5px;
                }

                div.item-wrap {
                    height: 257px;
                    width: 350px;
                    margin-top: 40px;
                    margin-left: 10px;
                }

                .cube {
                    position: relative;
                    height: 257px;
                    width: 350px;
                    -webkit-transition: -webkit-transform 0.5s linear;
                    -webkit-transform-style: preserve-3d;
                    -moz-transition: -moz-transform 0.5s linear;
                    -moz-transform-style: preserve-3d;
                    -o-transition: -o-transform 0.5s linear;
                    -o-transform-style: preserve-3d;
                    -ms-transition: -ms-transform 0.5s linear;
                    -ms-transform-style: preserve-3d;
                    transition: transform 0.5s linear;
                    transform-style: preserve-3d;  

                    pointer-events: none;
                }

                .item-wrap a{
                    text-decoration: none;
                }

                .cube a{
                    position: relative;
                    height: 100%;
                    width: 100%;
                }

                .face {
                    position: absolute;
                    height: 100%;
                    width: 100%;
                    background-color: rgba(128, 128, 128, 0.36);

                }

                .cube .one  {
                    -webkit-transform: rotateX(90deg) translateZ(128px);
                    -webkit-backface-visibility: hidden;
                    -moz-transform: rotateX(90deg) translateZ(128px);
                    -moz-backface-visibility: hidden;
                    -o-transform: rotateX(90deg) translateZ(128px);
                    -o-backface-visibility: hidden;
                    -ms-transform: rotateX(90deg) translateZ(128px);
                    -ms-backface-visibility: hidden;
                    transform: rotateX(90deg) translateZ(128px);
                    backface-visibility: hidden;
                }

                .cube .one .item-info {
                    margin: 10px;
                }

                .cube .one .item-info .header {
                    margin-top: 10px;
                    padding-left: 25px;
                }

                .cube .one .item-info .name {
                    font-size: 15px;
                    color: #5A52F3;
                    padding-left: 30px;
                    padding-top: 5px;
                    padding-right: 10px;
                }

                .cube .one .item-info .header .author {
                    font-size: 12px;
                    color: #66757f;
                }

                .cube .one .content {
                    margin-top: 15px;
                    margin-left: 30px;
                    margin-right: 30px;
                    margin-bottom: 10px;
                    height: 130px;
                    color: #292f33;
                    font-size: 12px;
                    font-weight: 400;
                    line-height: 20px;
                    word-wrap: break-word;
                    text-align: left!important;
                }

                .cube .one .link{
                    margin-left: 150px;
                }

                .cube .two {
                    -webkit-transform: translateZ(128px);
                    -webkit-backface-visibility: hidden;
                    -moz-transform: translateZ(128px);
                    -moz-backface-visibility: hidden;
                    -o-transform: translateZ(128px);
                    -o-backface-visibility: hidden;
                    -ms-transform: translateZ(128px);
                    -ms-backface-visibility: hidden;
                    transform: translateZ(128px);
                    backface-visibility: hidden;
                }

                .top-menu{
                    margin-top: 5px;
                }

                .top-menu-main {
                    position: relative;
                    float: left;
                    list-style: none;
                    margin: 0;
                    z-index: 100;
                }

                ul.menu {
                    list-style:none;
                    bottom: 23px;
                    cursor: pointer;
                    display: none;
                    min-width: 100%;
                    position: absolute;
                    padding: 0px;
                    margin: 0px;
                    white-space: nowrap;
                    -moz-box-shadow: 0 0 8px #333;
                    -webkit-box-shadow: 0 0 8px #333;
                    -o-box-shadow: 0 0 8px #333;
                    -ms-box-shadow: 0 0 8px #333;
                    box-shadow: 0 0 8px #333;
                    margin-left: -10px;
                }

                ul.menu  a {
                    margin: 0px;
                    padding: 5px 10px 5px 10px;
                    background: #333;
                    color: white;
                    white-space: nowrap;
                    list-style: none;
                    display: block;
                    font-size: 12px;
                }

                ul.menu  a:hover {
                    background: linear-gradient(#ccc, #999);
                    color: black;
                    text-decoration: none;
                }

                .wishlist {
                    padding-top: 15px;
                    padding-left: 25px;
                }

                .wishlist a,
                .wishlist span{
                    color: #0099B9 !important;
                }

                .sociallinks {
                    padding-top: 15px;
                    margin-left: -40px;
                }

                .sociallinks li{
                    display: inline;
                    list-style-type: none;
                    padding-right: 2px;
                }

                .cjs-twitter-button {
                    position: absolute !important;
                }
